parameters.jsCode:
// Reportar inicio del proceso
const jobId = $input.first().json.job_id;
const progressUrl = $input.first().json.progress_url;

// Enviar progreso inicial
if (jobId && progressUrl) {
  $http.post(progressUrl, {
    job_id: jobId,
    status: 'running',
    step: 'Iniciando proceso de cotización',
    progress: 10
  });
}

return $input.all();

// Filtrar sitios disponibles según el tipo de servicio
const tipoServicio = $('Webhook').first().json.tipo_servicio;
const categoria = tipoServicio === 'sitio_web' ? 'Sitio Web' : 'Automatización';

const items = $input.all()
  .filter(item => {
    const row = item.json;
    const disponible = row.Disponible === 'Sí' || row.Disponible === 'Yes' || row.Disponible === true;
    const categoriaMatch = row.Categoría === categoria || 
                          row.Categoría === 'Sitio Web' || 
                          row.Categoría === 'E-commerce' ||
                          (tipoServicio === 'automatizacion' && row.Categoría === 'Automatización');
    return disponible && categoriaMatch;
  })
  .map(item => ({
    json: {
      id: item.json.ID || item.json.id,
      nombre: item.json.Nombre || item.json.nombre,
      descripcion: item.json.Descripción || item.json.descripcion,
      precio: item.json.Precio || item.json.precio,
      categoria: item.json.Categoría || item.json.categoria
    }
  }));

return items.length > 0 ? items : [{ json: { error: 'No se encontraron servicios disponibles' } }];

// Combinar datos del cliente con opciones de servicios
const datosCliente = $('Webhook').first().json;
const opcionesServicios = $input.all().map(item => item.json);

// Reportar progreso
if (datosCliente.job_id && datosCliente.progress_url) {
  $http.post(datosCliente.progress_url, {
    job_id: datosCliente.job_id,
    status: 'running',
    step: `Se encontraron ${opcionesServicios.length} opciones de servicios`,
    progress: 50
  });
}

return [{
  json: {
    cliente: {
      nombre: datosCliente.nombre,
      email: datosCliente.email,
      telefono: datosCliente.telefono || '',
      tipo_servicio: datosCliente.tipo_servicio,
      mensaje: datosCliente.mensaje || ''
    },
    opciones_servicios: opcionesServicios,
    job_id: datosCliente.job_id,
    progress_url: datosCliente.progress_url
  }
}];

